INSERT INTO `pg_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(15439, 1463, 'main_publications', '<style type="text/css"><!--\r\n@page { size: 21cm 29.7cm; margin: 2cm }\r\n		P { margin-bottom: 0.21cm; direction: ltr; color: #000000; line-height: 115%; widows: 2; orphans: 2 }\r\n		P.western { font-family: "Calibri", sans-serif; font-size: 11pt; so-language: pl-PL }\r\n		P.cjk { font-family: "Calibri", sans-serif; font-size: 11pt }\r\n		P.ctl { font-family: "Times New Roman", serif; font-size: 11pt; so-language: ar-SA }\r\n--></style><span style="font-family: Arial,sans-serif;"><span style="font-size: small;"><b>wystawy indywidualne</b></span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">1996</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa malarstwa i grafiki „2 x zdarzenia”, górnośląskie centrum kultury, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">1998</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafik pt.: „album”, galeria „brama”, gliwice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2001</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafik pt.: „portret”, galeria „evviva l’arte”, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2002</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafik pt.: „po tej stronie”, galeria muzeum historii katowic, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2003</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa cyklu grafik pt.: „album”, aula główna akademii ekonomicznej, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2004</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">obrona pracy doktorskiej, akademia sztuk pięknych w katowicach, dyscyplina: grafika, tytuł cyklu graficznego: „prawdziwe życie’, aula asp katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2006</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafik pt.: „podróże przyziemne”, galeria miejska, ratusz, gliwice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2008</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafik, obiektów i mediów „siły grawitacji”, galeria sztuki „mm”, chorzów</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2009</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafik, obiektów i mediów „siły grawitacji”, galeria związku polskich artystów plastyków „po schodach”, gliwice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafik, obiektów i mediów „siły grawitacji”, galeria teatru „ateneum”, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2010</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafiki i mediów „wejścia-wyjścia”, galeria „w stolarni”, muzeum śląskie, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafiki i mediów „wejścia-wyjścia”, galeria w centrum sztuki „impart”, wrocław</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2012</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafiki i mediów „wejścia-wyjścia”, galeria „za szybą”, akademia sztuk pięknych im. eugeniusza gepperta, Wrocław</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2014</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafik, rysunków i mediów „skwer skaczącej gwiazdy”, galeria związku polskich artystów plastyków, centrum nowych technologii, gliwice</span></span>\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;"><span style="text-decoration: underline;"><b>wystawy zbiorowe</b></span></span></span>\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">1996</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa grafik studentów akademii sztuk pięknych w krakowie, wydziału grafiki w katowicach, hattingen, essen (niemcy)</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">1997</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa z okazji 50-lecia państwowego liceum sztuk plastycznych w katowicach, górnośląskie centrum kultury, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa podyplomowa grafik i malarstwa, górnośląskie centrum kultury, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">23 ogólnopolska wystawa malarstwa „bielska jesień”: „krajobraz końca xx wieku”, biuro wystaw artystycznych, bielsko-biała</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">1998</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa pokonkursowa „grafika roku 1997”, akademia sztuk pięknych, kraków, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">1999</span></span>\r\n\r\n‘<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">biennale grafiki”, galeria związku polskich artystów plastyków, poznań</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa pokonkursowa „praca roku 1998”, galeria związku polskich artystów plastyków okręgu katowickiego „art nova 2”, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa „pracownia profesora jacka Rykały”, galeria sztuki współczesnej bwa, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2000</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawy „5 x 5 i młoda grafika polska”:</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">galeria europejskiej akademii sztuki, warszawa</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">galeria sztuki współczesnej, ostrołęka</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">galeria „mała”, lublin</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2001</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa „sztuka i edukacja, pedagodzy, przyjaciele i absolwenci szkoły sztuk pięknych w Zabrzu”, galeria miejska, zabrze</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2002</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa pokonkursowa „11 salonu plastyki - egeria 2002”, galeria sztuki współczesnej bwa, ostrów wielkopolski</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">6 jesienna wystawa stowarzyszenia chorzowskich artystów plastyków, galeria sztuki „mm”, chorzów</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2003</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawy z okazji 5-lecia schap:</span></span>\r\n\r\n„<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">stara kordegarda” łazienki królewskie, warszawa</span></span>\r\n\r\n„<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">zapiecek u jędrysa”, wisła</span></span>\r\n\r\n„<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">espace matisse”, creil (francja)</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">galeria sztuki „mm”, chorzów</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2004</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">7 jesienna wystawa stowarzyszenia chorzowskich artystów plastyków, galeria sztuki „mm”, chorzów </span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2005</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa stowarzyszenia chorzowskich artystów plastyków, gdynia</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2006</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa „60 lat związku polskich artystów plastyków w gliwicach 1945-2005”, galeria sztuki „dom plastyków”, gliwice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa „pracowników i przyjaciół z okazji 50-lecia ogólnokształcącej szkoły sztuk pięknych w Zabrzu”, galeria miejska, zabrze</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa „podróże na wschód”, galeria „almi art.”, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">8 jesienna wystawa stowarzyszenia chorzowskich artystów plastyków, galeria sztuki „mm”, chorzów</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa stowarzyszenia chorzowskich artystów plastyków, galeria w domu polskim, wilno (litwa)</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2007</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa stowarzyszenia chorzowskich artystów plastyków, iserlohn (niemcy)</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa „60 lat ogólnokształcącej szkoły sztuk pięknych w katowicach”, aula ossp, katowice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">2008</span></span>\r\n\r\n<span style="font-family: Arial,sans-serif;"><span style="font-size: small;">wystawa członków związku polskich artystów plastyków okręgu gliwicko-zabrzańskiego „3. rocznica śmierci papieża jana pawła 2”, centrum edukacyjne im. jana pawła 2, gliwice</span></span>\r\n\r\n&nbsp;\r\n\r\n<span st